mapper.select:
SELECT t_sprinkler.*
FROM t_sprinkler

    deleted_flag = #{queryForm.deletedFlag}

        AND (INSTR(sprinkler_serial,#{queryForm.keywords}) OR INSTR(warehouse_date,#{queryForm.keywords}) OR INSTR(history,#{queryForm.keywords}))


        AND DATE_FORMAT(warehouse_date, '%Y-%m-%d') >= #{queryForm.startTime}


        AND DATE_FORMAT(warehouse_date, '%Y-%m-%d') <= #{queryForm.endTime}


        AND disabled_flag = #{queryForm.disabledFlag}



    ORDER BY create_time DESC

SELECT *
FROM t_sprinkler
WHERE sprinkler_serial = #{sprinklerSerial}
AND deleted_flag = #{deletedFlag}

    AND sprinkler_id != #{excludeSprinklerId}

SELECT t_sprinkler.*
FROM t_sprinkler

    deleted_flag = #{queryForm.deletedFlag}

        AND (INSTR(sprinkler_serial,#{queryForm.keywords}) OR INSTR(warehouse_date,#{queryForm.keywords}) OR INSTR(history,#{queryForm.keywords}))


        AND DATE_FORMAT(warehouse_date, '%Y-%m-%d') >= #{queryForm.startTime}


        AND DATE_FORMAT(warehouse_date, '%Y-%m-%d') <= #{queryForm.endTime}


        AND disabled_flag = #{queryForm.disabledFlag}



    ORDER BY create_time DESC

mapper.update:
UPDATE t_sprinkler
SET deleted_flag = #{deletedFlag}
WHERE sprinkler_id = #{sprinklerId}
