mapper.select:
SELECT er.role_id
FROM t_role_employee er
WHERE er.employee_id = #{employeeId}

SELECT t_employee.employee_id, t_employee.login_name, t_employee.login_pwd, t_employee.actual_name, t_employee.phone, t_employee.department_id, t_employee.disabled_flag, t_employee.remark, t_employee.update_time, t_employee.create_time FROM t_role_employee left join t_employee on t_role_employee.employee_id = t_employee.employee_id left join t_role on t_role_employee.role_id = t_role.role_id
AND ( INSTR(t_employee.actual_name,#{queryForm.keywords}) OR INSTR(t_employee.phone,#{queryForm.keywords}) OR INSTR(t_employee.login_name,#{queryForm.keywords}) )
AND t_role_employee.role_id = #{queryForm.roleId}

mapper.delete:
DELETE
FROM t_role_employee
WHERE role_id = #{roleId}
  and employee_id = #{employeeId}

DELETE FROM t_role_employee
WHERE role_id = #{roleId} and employee_id in

    #{item}
