mapper.select:
select *
from t_feedback


        AND (
        INSTR(feedback_content,#{query.searchWord})
        OR INSTR(create_name,#{query.searchWord})
        )


        AND DATE_FORMAT(create_time, '%Y-%m-%d') >= #{query.startDate}


        AND DATE_FORMAT(create_time, '%Y-%m-%d') <= #{query.endDate}



    order by create_time desc
