mapper.select:
select n.id, n.title, n.create_user, e.actual_name as createUserName, n.send_status, n.create_time from t_notice n left join t_employee e on e.id = n.create_user
AND DATE_FORMAT(n.create_time, '%Y-%m-%d') >= #{queryDTO.startDate}
AND DATE_FORMAT(n.create_time, '%Y-%m-%d') <= #{queryDTO.endDate}
AND INSTR(n.title,#{queryDTO.title})
AND n.deleted = #{queryDTO.deleted}
ORDER by n.create_time desc

select n.id, n.title, n.create_user, n.send_status, e.actual_name as createUserName, n.create_time, nrr.create_time as receiveTime from t_notice n left join t_employee e on e.id = n.create_user left join t_notice_receive_record nrr on nrr.notice_id=n.id and nrr.employee_id=#{queryDTO.employeeId}
AND DATE_FORMAT(n.create_time, '%Y-%m-%d') >= #{queryDTO.startDate}
AND DATE_FORMAT(n.create_time, '%Y-%m-%d') <= #{queryDTO.endDate}
AND INSTR(n.title,#{queryDTO.title})
AND n.send_status = #{queryDTO.sendStatus}
ORDER by n.create_time desc

mapper.update:
UPDATE t_notice
set deleted = #{deletedFlag}
WHERE id =#{id}

mapper.select:
select
count(1)
from t_notice where send_status = #{sendStatus}

SELECT nrr.employee_id, count(1) as readCount from t_notice_receive_record nrr where nrr.employee_id in
#{item}
GROUP BY nrr.employee_id
