mapper.update:
update t_notice
set deleted_flag = true
where notice_id = #{noticeId}

mapper.select:
select t_notice.*, t_notice_type.notice_type_name, (select count(*) from t_notice_view_record where t_notice_view_record.employee_id = #{requestEmployeeId} and t_notice_view_record.notice_id = t_notice.notice_id) as viewFlag from t_notice left join t_notice_type on t_notice.notice_type_id = t_notice_type.notice_type_id
t_notice.notice_id in (select t_notice_visible_range.notice_id from t_notice_visible_range where (t_notice_visible_range.data_type = #{departmentDataType}
and t_notice_visible_range.data_id in
#{item}
) or ( t_notice_visible_range.data_type = #{employeeDataType} and t_notice_visible_range.data_id = #{requestEmployeeId} ) )
and t_notice.all_visible_flag = true and t_notice.deleted_flag = #{deletedFlag} and t_notice.publish_time < now()
AND t_notice_type.notice_type_id = #{query.noticeTypeId}
AND ( INSTR(t_notice.title,#{query.keywords}) OR INSTR(t_notice.author,#{query.keywords}) OR INSTR(t_notice.documentNumber,#{query.keywords}) OR INSTR(t_notice.source,#{query.keywords}) )
AND DATE_FORMAT(t_notice.publish_time, '%Y-%m-%d') >= DATE_FORMAT(#{query.publishTimeBegin}, '%Y-%m-%d')
AND DATE_FORMAT(t_notice.publish_time, '%Y-%m-%d') <= DATE_FORMAT(#{query.publishTimeEnd}, '%Y-%m-%d')
AND viewFlag = 0
order by t_notice.publish_time desc

select * from ( select t_notice.*, t_notice_type.notice_type_name, (select count(*) from t_notice_view_record where t_notice_view_record.employee_id = #{requestEmployeeId} and t_notice_view_record.notice_id = t_notice.notice_id) as viewFlag from t_notice left join t_notice_type on t_notice.notice_type_id = t_notice_type.notice_type_id
t_notice.notice_id in (select t_notice_visible_range.notice_id from t_notice_visible_range where (t_notice_visible_range.data_type = #{departmentDataType}
and t_notice_visible_range.data_id in
#{item}
) or ( t_notice_visible_range.data_type = #{employeeDataType} and t_notice_visible_range.data_id = #{requestEmployeeId} ) )
and t_notice.all_visible_flag = true and t_notice.deleted_flag = #{deletedFlag} and t_notice.publish_time < now()
AND t_notice_type.notice_type_id = #{query.noticeTypeId}
AND ( INSTR(t_notice.title,#{query.keywords}) OR INSTR(t_notice.author,#{query.keywords}) OR INSTR(t_notice.documentNumber,#{query.keywords}) OR INSTR(t_notice.source,#{query.keywords}) )
AND DATE_FORMAT(t_notice.publish_time, '%Y-%m-%d') >= DATE_FORMAT(#{query.publishTimeBegin}, '%Y-%m-%d')
AND DATE_FORMAT(t_notice.publish_time, '%Y-%m-%d') <= DATE_FORMAT(#{query.publishTimeEnd}, '%Y-%m-%d')
) t where viewFlag = 0 order by t.publish_time desc

select t_notice_view_record.*, t_employee.actual_name as employeeName, t_department.name as departmentName from t_notice_view_record left join t_employee on t_employee.employee_id = t_notice_view_record.employee_id left join t_department on t_department.department_id = t_employee.department_id where notice_id = #{queryForm.noticeId}
AND ( INSTR(t_employee.actual_name,#{queryForm.keywords}) OR INSTR(t_notice_view_record.first_ip,#{queryForm.keywords}) OR INSTR(t_notice_view_record.first_user_agent,#{queryForm.keywords}) OR INSTR(t_notice_view_record.last_ip,#{queryForm.keywords}) OR INSTR(t_notice_view_record.last_user_agent,#{queryForm.keywords}) )
and t_department.department_id = #{queryForm.departmentId}
order by t_notice_view_record.update_time desc,t_notice_view_record.create_time desc

select count(*)
from t_notice_view_record
where notice_id = #{noticeId}
  and employee_id = #{employeeId}

mapper.update:
update t_notice_view_record
set page_view_count = page_view_count + 1,
    last_ip         = #{ip},
    last_user_agent = #{userAgent}
where notice_id = #{noticeId}
  and employee_id = #{employeeId}
